import React from 'react';
import { Link } from 'react-router-dom';
import { FaChat, FaBell, FaMoon, FaSun } from 'react-icons/fa';
import { FaComments } from 'react-icons/fa';
import './css/Navbar.css';
import logo from '../assets/logos/logo-3.png';


const Navbar = () => {
  // State for mode toggle (light/dark)
  const [darkMode, setDarkMode] = React.useState(false);

  const toggleMode = () => {
    setDarkMode(!darkMode);
    // Implement mode switching logic as needed
    // For example, adding a class to the body for dark mode styles
    if (!darkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  };

  return (
    <nav className="navbar">
      {/* Left Section: Logo and Text */}
      <div className="navbar-left">
        <img src={logo} alt="Logo" className="logo-image" />
        <div className="logo-text">QuillQuest</div>
      </div>

      {/* Center Section: Navigation Links */}
      <div className="navbar-center">
        <ul className="nav-links">
          <li><Link to="/essay-relay">Essay Relay</Link></li>
          <li><Link to="/home">Home</Link></li>
          <li><Link to="/create-post">Create Post</Link></li>
        </ul>
      </div>

      {/* Right Section: Icons and User */}
      <div className="navbar-right">
        <button className="icon-button" aria-label="Chat">
          <FaChat />
        </button>
        <button className="icon-button" aria-label="Notifications">
          <FaBell />
        </button>
        <button className="icon-button" onClick={toggleMode} aria-label="Toggle Mode">
          {darkMode ? <FaSun /> : <FaMoon />}
        </button>
        <div className="user-icon">U</div>
      </div>
    </nav>
  );
};

export default Navbar;
