const Home = () => {
    // Sample Posts Data
    const posts = [
      {
        username: 'username0',
        time: '14 minutes ago',
        title: 'Why I believe the bald eagle shouldn’t be the symbol of the USA',
        prompt: 'Is the bald eagle the best symbol to represent the US?',
        content: 'The bald eagle, with its majestic presence and powerful flight, has long stood as a symbol of the United States...',
        likes: '1.4K',
        comments: '1.4K',
        imageUrl: 'https://via.placeholder.com/600x400' // Replace with actual image URLs or imports
      },
      {
        username: 'QuillBot',
        time: '5 minutes ago',
        title: 'Why I believe the bald eagle is the perfect symbol of the USA',
        prompt: 'Is the bald eagle the best symbol to represent the US?',
        content: 'The bald eagle, with its majestic presence and powerful flight, has long stood as a symbol of the United States...',
        likes: '1.4K',
        comments: '1.4K',
        imageUrl: 'https://via.placeholder.com/600x400' // Replace with actual image URLs or imports
      },
    ];
  
    return (
      <div className="home-container">
        <Navbar />
        <div className="post-feed">
          {posts.map((post, index) => (
            <Post 
              key={index}
              username={post.username}
              time={post.time}
              title={post.title}
              image={post.imageUrl}
              prompt={post.prompt}
              content={post.content}
              likes={post.likes}
              comments={post.comments}
            />
          ))}
        </div>
      </div>
    );
  };
  
  export default Home;
